Modal.tsx.code:
import { Transition, Dialog } from '@headlessui/react';
import React, { Fragment, useState } from 'react';

interface ModalProps {
  children: React.ReactNode;
  title: string;
  isOpen: boolean;
  setIsOpen: React.Dispatch<React.SetStateAction<boolean>>;
}

export default function Modal({
  children,
  title,
  isOpen,
  setIsOpen,
}: ModalProps) {
  function closeModal() {
    setIsOpen(false);
  }

  function openModal() {
    setIsOpen(true);
  }
  return (
    <Transition appear show={isOpen} as={Fragment}>
      <Dialog
        as='div'
        className='fixed inset-0 z-10 overflow-y-auto'
        onClose={closeModal}>
        <div className='min-h-screen px-4 text-center'>
          <Transition.Child
            as={Fragment}
            enter='ease-out duration-300'
            enterFrom='opacity-0'
            enterTo='opacity-100'
            leave='ease-in duration-200'
            leaveFrom='opacity-100'
            leaveTo='opacity-0'>
            <Dialog.Overlay className='fixed inset-0 backdrop-blur-lg backdrop-filter' />
          </Transition.Child>

          {/* This element is to trick the browser into centering the modal contents. */}
          <span
            className='inline-block h-screen align-middle'
            aria-hidden='true'>
            &#8203;
          </span>
          <Transition.Child
            as={Fragment}
            enter='ease-out duration-300'
            enterFrom='opacity-0 scale-95'
            enterTo='opacity-100 scale-100'
            leave='ease-in duration-200'
            leaveFrom='opacity-100 scale-100'
            leaveTo='opacity-0 scale-95'>
            <div className='inline-block text-left w-full max-w-xl align-middle transition-all transform bg-black text-gray-50 rounded-sm shadow-xl border border-gray-700'>
              <div className='p-4 flex border-b border-gray-800 justify-between items-center'>
                <Dialog.Title as='h3' className='text-lg font-medium leading-6'>
                  {title}
                </Dialog.Title>
                <button
                  onClick={closeModal}
                  className='text-xl hover:bg-gray-800 rounded-sm px-2'>
                  &times;
                </button>
              </div>
              <div className='content p-4'>{children}</div>
            </div>
          </Transition.Child>
        </div>
      </Dialog>
    </Transition>
  );
}

